11.query:
fetch bizevents
| filter event.type == "com.easytrade.sell-assets" or event.type == "com.easytrade.buy-assets"
| parse event.type, "'com.easytrade.' ALPHA:tradeType"
//| summarize trade = count(), by:{bin(timestamp, 1h),tradeType}
| summarize buy = countif(tradeType == "buy"), sell = countif(tradeType == "sell"), by:{bin(timestamp, 15m)}
