grail.canonicalQuery:
fetch events
| filter event.provider == "Dynatrace"
| filter event.type == "VULNERABILITY_STATE_REPORT_EVENT"
| filter event.level == "ENTITY"
| filter contains(affected_entity.vulnerable_component.name, "apache")
| fieldsAdd "match", alias:matcher
| lookup 
	[
		fetch events
		| filter event.provider == "Dynatrace"
		| filter event.type == "VULNERABILITY_STATE_REPORT_EVENT"
		| filter event.level == "ENTITY"
		| filter contains(affected_entity.vulnerable_component.name, "apache")
		| fields {timestamp, alias:maxTimestamp}, {"match", alias:matcher}
		| limit 1
	], sourceField:matcher, lookupField:matcher, fields:{maxTimestamp}
| filter timestamp == maxTimestamp
| summarize by:{affected_entity.id}, {{countIf(vulnerability.davis_assessment.vulnerable_function_status == "IN_USE"), alias:`Function in use`}, {countIf(vulnerability.davis_assessment.vulnerable_function_status == "NOT_IN_USE"), alias:`Function not in use`}, {countIf(vulnerability.davis_assessment.vulnerable_function_status == "NOT_AVAILABLE"), alias:`Function not available`}, {countIf(vulnerability.davis_assessment.exposure_status == "PUBLIC_NETWORK"), alias:`Internet exposure`}, {countIf(vulnerability.davis_assessment.data_assets_status), alias:`Data assets in reach`}, {takeAny(affected_entity.type), alias:Type}}
| summarize {countIf(`Function in use` > 0), alias:`Function in use`}, {countIf(`Function not in use` > 0), alias:`Function not in use`}, {countIf(`Function not available` > 0), alias:`Function not available`}, {countIf(`Internet exposure` > 0), alias:`Internet exposure`}, {countIf(`Data assets in reach` > 0), alias:`Data assets in reach`}, {arraySize(collectDistinct(affected_entity.id)), alias:`Affected entities`}, {collectDistinct(Type), alias:Types}
| fields Types, `Affected entities`, {if(`Internet exposure` > 0, concat("â—", `Internet exposure`), else:0), alias:`With internet exposure`}, {if(`Data assets in reach` > 0, concat("â—", `Data assets in reach`), else:0), alias:`With data assets in reach`}, {if(`Function in use` > 0, concat("â—", `Function in use`), else:if(`Function not in use` > 0, "Not in use", else:"No data")), alias:`With function in use`}

input.value:
fetch events
| filter event.provider=="Dynatrace"
| filter event.type=="VULNERABILITY_STATE_REPORT_EVENT"
| filter event.level=="ENTITY"
| filter vulnerability.resolution.status=="OPEN"
| filter contains(affected_entity.vulnerable_component.name,"apache") // filter for explicit component
// filter only the latest snapshot data
| fieldsAdd matcher="match"
| lookup [
fetch events
| filter event.provider=="Dynatrace"
| filter event.type=="VULNERABILITY_STATE_REPORT_EVENT"
| filter event.level=="ENTITY"
| filter vulnerability.resolution.status=="OPEN"
| filter contains(affected_entity.vulnerable_component.name,"apache") // filter for explicit component
| fields 
maxTimestamp=timestamp,
matcher="match"
| limit 1
], sourceField:matcher, lookupField:matcher, fields:{maxTimestamp}
| filter timestamp==maxTimestamp
//end of filtering for the latest snapshot
//add ownership information
| lookup [
fetch dt.entity.process_group
| fieldsAdd tags
], sourceField:dt.entity.process_group, lookupField:id, fields:{tags}
| parse toString(tags), "LD ('owner:'|'owner\\\\:') (SPACE)? LD:Team ('\"')"
| fieldsAdd Team=if(isNotNull(Team),Team,else:"-")
| fieldsRemove lookup.id, lookup.entity.name, Tags
// end of adding ownership info
| summarize {
`Entity names`=collectDistinct(affected_entity.name),
`Component names`=arrayRemoveNulls(collectDistinct(affected_entity.vulnerable_component.name))
}, by: {Team}
| fields Team, `Affected entities`=arraySize(`Entity names`), `Components`=arraySize(`Component names`)
| sort {`Affected entities`, direction:"descending"}, {`Components`, direction:"descending"}

